
-- node 分页数据
-- 创建数据库
create database nodepages charset=utf8;
-- 切换到数据库
use nodepages;	
-- 创建表 
create table pages(
    id int auto_increment primary key not null,
    modelname varchar(30),
    img varchar(200),
    title varchar(30),
    visitNum varchar(200),
    star varchar(200),
    timedate date
);

